import React, { Component } from 'react';
import './timeline.css';

class TimelineSpecific extends Component {

    constructor(props) {
        super(props);
        this.state = {
            type: props.type,
            typeVal: 0,
            types: {0: "Programming", 1: "Aviation", 2: "Desgin", 3: "PC Building"}
        }
    }

    handleClick() {
        this.setState({
            typeVal: (this.state.typeVal + 1) % 4,
            type: this.state.types[this.state.typeVal]
        })
    }

    render() {

        return(

            <button onClick={this.handleClick}>{this.state.type}</button>
            
        )

    }

};

export default TimelineSpecific;
