import React, { Component } from 'react';
import backgroundImage from "../../images/background.png";
import MyPicture from "../../images/me filter.png";
import './about.css';
// import BasicInfo from "./basic_info";
import Navbar from "../navbar";

function getYears(year) {
    let d = new Date();
    if (Math.floor((d.getTime() - 31536000000 * (year - 1970)) / 31536000000) < 1) {
        return ("<1");
    };
    return Math.floor((d.getTime() - 31536000000 * (year - 1970)) / 31536000000);
};

class About extends Component {

    render() {
        return(
            <div className="App">

                <img src={backgroundImage} className="backgroundImage"></img>

                <Navbar page={this.props.page} />

                <div className="aboutpage">
                    {/* <div className="detailedInfo"></div> */}
    
                    <div className="textWrapper">
                        <div className="invisibleObject"></div>
                        <p className="aboutMeText">About Me</p>
                        <div className="lineDesign"></div>
                        <p className="detailedText">I'm <span className="orangeText">Poh Jun Kang</span>, Based in Singapore and I can help you with <span className="orangeText">Programming, Designing, Flying</span> and <span className="orangeText">PC Building</span>.</p>
                        <p className="detailedText">
                            <span className="orangeText">Progamming Skills</span> <span className="footnotes">(Years of experience in brackets, ordered by most to least proficient)</span>
                            <br></br>
                            <br></br>
                            <br></br>
                            &nbsp;&nbsp;&nbsp;
                            Proficient Languages - 
                            <span className="orangeText"> Python</span> ({getYears(2016)}), 
                            <span className="orangeText"> Javascript</span> ({getYears(2019)}), 
                            <span className="orangeText"> HTML5</span> ({getYears(2019)}), 
                            <span className="orangeText"> CSS</span> ({getYears(2019)}), 
                            <span className="orangeText"> <i>jQuery</i></span> ({getYears(2019)}), 
                            <span className="orangeText"> React</span> ({getYears(2022)}), and
                            <span className="orangeText"> SQL</span> ({getYears(2019)}). 
                            <br></br>
                            <br></br>
                            &nbsp;&nbsp;&nbsp;
                            Less Proficient Languages - 
                            <span className="orangeText"> C++</span> ({getYears(2019)}), 
                            <span className="orangeText"> NoSQL</span> ({getYears(2019)}), 
                            <span className="orangeText"> Lua</span> ({getYears(2018)}), and
                            <span className="orangeText"> PHP</span> ({getYears(2020)}). 
                        </p>
                    </div>
                    <img src={MyPicture} className="MyPicture"></img>
                </div>
                {/* <p className="pastExperience">My Past Experiences</p>

                <div className="appMain">
                    <BasicInfo />
                </div> */}

            </div>
        )
    }

};

export default About;
