import React, { Component } from 'react';
import backgroundImage2 from "../../images/background 2.png";
import './timeline.css';

function TimelineSpecificContent(props) {
    console.log(props.typeValue)
    
    let currentContent = [];
    for (let i = 0; i < (props.content[props.typeValue]).length; i++) {
        currentContent.push(
            <>
                <h1>{props.content[props.typeValue][i][0]}</h1>
                <p>{props.content[props.typeValue][i][2]}</p>
            </>
        );
    }

    return(
        <div>

        </div>
    )

};

class TimelineSpecific extends Component {

    constructor(props) {
        super(props);
        this.state = {
            type: props.type,
            typeVal: 0,
            types: {0: "Programming", 1: "Aviation", 2: "Desgin", 3: "PC Building"},
            content: {
                0: [
                    ["My Personal Website", 2022, "After coming back from my trip to the USA, I had a lot of free time in the Army before I finish my service. That's where I decided to make use of my newly learnt knowledge on React to rebuild a second version of my website."],
                    ["Learning React", 2022, "During my National Service trip to USA, I decided to learn React, specifically ReactJS, to further expand my Web Programming knowledge."]
                ],
                1: [

                ],
                2: [

                ],
                3: [

                ]
            },
            images: {backgroundImage2}
        };
    }

    changeType = () => {
        this.setState({
            typeVal: (this.state.typeVal + 1) % 4,
            type: this.state.types[(this.state.typeVal + 1) % 4]
        });
    }

    render() {

        return(

            <>
                <button onClick={this.changeType}>{this.state.type}</button>
    
                <TimelineSpecificContent typeValue={this.state.typeVal} value={this.state.types[this.state.typeVal]} content={this.state.content} />
            </>
            
        )

    }

};

export default TimelineSpecific;
