import React, { Component } from 'react';
import { NavLink as Link } from "react-router-dom";
import './navbar.css';

function checkColour(page, currentPage) {
    return(page == currentPage ? "#ffffff" : "#d2d2d2");
};

class Navbar extends Component {

    render() {
        return(
            <div className="navbarBG">
                <Link to="/" className="links">
                    <p className="linksText" style={{color: checkColour(this.props.page, "1")}}>Home</p>
                </Link>
                <Link to="/timeline" className="links">
                    <p className="linksText" style={{color: checkColour(this.props.page, "2")}}>Timeline</p>
                </Link>
                <Link to="/about" className="links">
                    <p className="linksText" style={{color: checkColour(this.props.page, "3")}}>About Me</p>
                </Link>
                <Link to="/contact" className="links">
                    <p className="linksText" style={{color: checkColour(this.props.page, "4")}}>Contact</p>
                </Link>
            </div>
        )
    }
};

export default Navbar;
