import React from 'react';
import './timeline.css';

function TypeButtons(props) {
    
    let tempTypeImage = [...defaultIcons];
    tempTypeImage[typeVal] = activeIcons[typeVal];
    const [typeImage, setTypeImage] = useState(tempTypeImage);
    
    function mouseHoverTypes(e) { // hover over type header
        if (e.target.getAttribute("data-index") !== String(typeVal)) {
            tempTypeImage = [...typeImage];
            tempTypeImage[e.target.getAttribute("data-index")] = hoverIcons[e.target.getAttribute("data-index")];
            setTypeImage(tempTypeImage);
        };
    };
    
    function mouseLeaveTypes(e) { // unhover over type header
        if (e.target.getAttribute("data-index") !== String(typeVal)) {
            tempTypeImage = [...typeImage];
            tempTypeImage[e.target.getAttribute("data-index")] = defaultIcons[e.target.getAttribute("data-index")];
            setTypeImage(tempTypeImage);
        };
    };
    
    return(
        <button onClick={changeType} key={props.key} data-index={props.dataIndex} className={"timelineChanger fade slideInInitialR slideInR bolded flex-row " + props.toOrange + " "} onMouseEnter={mouseHoverTypes} onMouseLeave={mouseLeaveTypes}>
            <img src={typeImage[i]} className="timelineIcons" data-index={i}></img>
            <p className="timelineOptionsText" data-index={i}>{types[i]}</p>
        </button>
    )
};

export default TypeButtons
