import React, { Component, useState } from 'react';
import './timeline.css';

function TimelineSpecificContent(props) {
    let currentContent = [];
    console.log(props.content, props.typeValue);
    for (let i = 0; i < (props.content[props.typeValue]).length; i++) {
        currentContent.push(
            <div className="eachContent">
                <div className="eachContentDecorationDot"></div>
                <div className="eachContentWrapper">
                    <h1 className="eachContentHeader">{props.content[props.typeValue][i][0]}</h1>
                    <p className="eachContentYear">{props.content[props.typeValue][i][1]}</p>
                    <p className="eachContentDescription">{props.content[props.typeValue][i][2]}</p>
                </div>
            </div>
        );
    }

    return(
        <div className="timelineSpecificContent">
            {currentContent}
        </div>
    )

};

function TimelineSpecific(props) {

    const [type, setType] = useState(props.type); // the string related
    const [typeVal, setTypeVal] = useState(props.initialTimeline); // 0 - 3
    const types = {0: "Programming", 1: "Aviation", 2: "Desgin", 3: "PC Building"};
    const content = {
        0: [
            ["My Personal Website", "Oct 2022", "After coming back from my trip to the USA, I had a lot of free time in the Army before I finish my service. That's where I decided to make use of my newly learnt knowledge on React to rebuild a second version of my website."],
            ["Learning React", "Sep 2022", "During my National Service trip to USA, I decided to learn React, specifically ReactJS, to further expand my Web Programming knowledge."]
        ],
        1: [

        ],
        2: [

        ],
        3: [

        ]
    };

    function changeType() {
        setTypeVal((typeVal + 1) % 4);
        setType(types[(typeVal + 1) % 4]);
        props.changeImage(props.imageOptions[(typeVal + 1) % 4]);
    }

    
    return(

        <div className="timeline">

            <div className="timelineUI">
                <button onClick={changeType}>{type}</button>
                <div className="timelineLine"></div>
            </div>

            <TimelineSpecificContent typeValue={typeVal} value={types[typeVal]} content={content} />

        </div>
        
    )


};

export default TimelineSpecific;
